namespace Atc.Wpf.Tests.Helpers;

public class BitmapImageHelperTests
{
    [Theory]
    [InlineData(true, "data:image/png;base64,")]
    [InlineData(true, "base64,")]
    [InlineData(true, "")]
    [InlineData(false, "")]
    public void ConvertFromBase64(bool expected, string base64Value)
    {
        // Atc
        var actual = BitmapImageHelper.ConvertFromBase64(base64Value);

        // Assert
        if (expected)
        {
            Assert.NotNull(actual);
        }
        else
        {
            Assert.Null(actual);
        }
    }
}